import styled, { keyframes } from "styled-components"; // 1. Importar keyframes
import { StyledLinkButton } from "@/components/ui/Button";
import { PATHS } from "@/constants/paths";

// La imagen completa, codificada como texto.
const heroImageBase64 =
  "data:image/jpeg;base64,-";

// 2. Definimos una animación de entrada
const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const HeroWrapper = styled.section`
  color: white;
  height: 60vh;
  display: flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  position: relative;
  padding: 0 20px; /* Añadimos padding para evitar que el texto toque los bordes en móviles */

  background-image: linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.6)),
    // url("${heroImageBase64}");
    url("/images/fondo-hero.jpg"); /* Usamos una imagen local optimizada */

  background-size: cover;
  background-position: center;
`;

const HeroContent = styled.div`
  max-width: 800px;
  
  /* 3. Aplicamos la animación */
  animation: ${fadeIn} 1s ease-out forwards;

  h1 {
    font-size: 3.5rem;
    font-weight: 900;
    text-shadow: 2px 2px 8px rgba(0, 0, 0, 0.7);
    letter-spacing: 1px; /* 4. Añadimos espacio entre letras para un look más refinado */
  }

  p {
    font-size: 1.25rem;
    margin-top: 1rem; /* Añadimos un poco de espacio sobre el párrafo */
    margin-bottom: 2.5rem; /* Aumentamos el espacio bajo el párrafo */
    color: var(--color-text-muted);
    max-width: 650px; /* 5. Hacemos el párrafo más angosto que el título */
    margin-left: auto;
    margin-right: auto;
  }
`;

// 6. Creamos un componente de botón estilizado para añadirle efectos de hover
const AnimatedStyledLinkButton = styled(StyledLinkButton)`
  font-size: 1.2rem;
  padding: 15px 30px;
  transition: transform 0.2s ease-in-out, box-shadow 0.2s ease-in-out; /* Transición suave */

  &:hover {
    transform: translateY(-3px); /* Efecto de "levantar" el botón */
    box-shadow: 0 4px 20px rgba(0, 255, 255, 0.25); /* Sombra sutil con el color del botón */
  }
`;

function Hero() {
  return (
    <HeroWrapper>
      <HeroContent>
        {/* Aquí puedes usar una de las nuevas opciones de texto */}
        <h1 className="display-3">Innovación que Transforma</h1>
        <p className="lead">
          Potencia tu día a día con tecnología de vanguardia. Los mejores
          componentes y accesorios para llevar tus ideas al siguiente nivel.
        </p>
        <AnimatedStyledLinkButton to={PATHS.PRODUCTS} $variant="primary">
          Explorar Productos
        </AnimatedStyledLinkButton>
      </HeroContent>
    </HeroWrapper>
  );
}

export default Hero;
